import styled from 'styled-components'

const FlipCardContainer = styled.div`
  background-color: red;
  width: 300px;
  height: 300px;
  border: 1px solid #f1f1f1;
  perspective: 1000px;
  margin: 10px
`

export default function StyDiv(props) {

  console.log(props.id, "id")
  console.log(props.selectedCard, "selected")

  var color = props.id === props.selectedCard ? 'blue' : 'red'
  var deg = props.id === props.selectedCard ? '0' : '80'

  var styles = {width: "300px", height:"300px", background: color, transform: `rotateY(${deg}deg)`}

  
  
  return(<div style={styles}></div>)
}
